import moment from "moment-timezone";

export default function DisplayWeather(props) {
  const { info, forecast } = props;
  console.log(forecast);
  return (
    <>
      <div>
        <div>Weather</div>
        <div>
          City: {info.name}, {info.state}, {info.country}{" "}
        </div>
        <div>
          <img src={info.icon} />
        </div>
        <div>Temperature: {info.temp} Â°F </div>
        <div>Outside: {info.weather}</div>
        <div>Description: {info.description}</div>
      </div>

      <div>
        <div>Forecast</div>
        <div className="flex">
          {forecast
            ? forecast.map((time) => (
                <div className='flex flex-row'>
                  {moment
                    .unix(time.dt)
                    .tz("America/New_York")
                    .format("YYYY-MM-DD HH:mm")}
                 
                  Temp:{time.main.temp}
                  Weather: {time.weather[0].main}
                  Description: {time.weather[0].description}
                </div>
              ))
            : null}
        </div>
      </div>
    </>
  );
}
