import moment from "moment-timezone";

export default function DisplayWeather(props) {
  const { info, forecast } = props;
  console.log(forecast);
  const d = new Date();
  return (
    <>
      <div>
        <div>Weather</div>
        <div>
          City: {info.name}, {info.state}, {info.country}{" "}
        </div>
        <div>
          <img src={info.icon} />
        </div>
        <div>
          Day and Time: {d}
          {d.getHours()}
        </div>
        <div>Temperature: {info.temp} Â°F </div>
        <div>Outside: {info.weather}</div>
        <div>Description: {info.description}</div>
      </div>

      <div>
        <div>Forecast</div>
        <div className="flex">
          {forecast
            ? forecast.map((time) => (
                <>
                  <div>
                    {moment
                      .unix(time.dt)
                      .tz("America/New_York")
                      .format("YYYY-MM-DD HH:mm")}
                  </div>
                  <div>Temp:{time.main.temp}</div>
                  <div>Weather: {time.weather[0].main}</div>
                  <div>Description: {time.weather[0].description}</div>{" "}
                </>
              ))
            : null}
        </div>
      </div>
    </>
  );
}
